void oled_render_layer_state(void) {
    oled_write_ln_P(PSTR("Izumi\nv5.1\n\n"), false);
    oled_write_ln_P(PSTR("Layer"), false);
    switch (layer_state) {
        case L_BASE:
            oled_write_ln_P(PSTR("CLMK"), false);
            break;
        case L_QWERTY:
            oled_write_ln_P(PSTR("QWTY"), false);
            break;
        case L_NUM:
            oled_write_ln_P(PSTR("NUM"), false);
            break;
        case L_CODE:
            oled_write_ln_P(PSTR("CODE"), false);
            break;
        case L_ONESHOT:
            oled_write_ln_P(PSTR("1SHOT"), false);
            break;
        case L_NAV:
            oled_write_ln_P(PSTR("NAV"), false);
            break;
        case L_SYS:
            oled_write_ln_P(PSTR("SYS"), false);
            break;
        case L_FPS:
            oled_write_ln_P(PSTR("FPS"), false);
            break;
    }
    oled_write_ln_P(PSTR("\n"), false);
}

